import React, { Component } from "react";
import { MainButton } from "./MainButton";
import './WeeklyPhrase.css';

class WeeklyPhrase extends Component {
    state = {
        isHovered: false,
        imageLoaded: false,
        imageError: false
    }

    handleMouseEnter = () => {
        this.setState({ isHovered: true });
    }

    handleMouseLeave = () => {
        this.setState({ isHovered: false });
    }

    handleImageLoad = () => {
        this.setState({ imageLoaded: true });
    }

    handleImageError = () => {
        this.setState({ imageError: true });
    }

    render() {
        const { phrase, meaning, example, src, alt, emojis } = this.props;
        const { isHovered, imageLoaded, imageError } = this.state;

        const canShowImage = src && imageLoaded && !imageError;
        const showImage = isHovered && canShowImage;

        return (
            <div className="phrase-container">
                <div className="phrase-title">Phrase Of The Week</div>

                <div className="phrase-content">
                    <div className="phrase-text">
                        <p className="phrase-phrase">"{phrase}"</p>
                        <p className="phrase-meaning">
                            <span style={{fontWeight: '600', color: '#2d3436'}}>Meaning:</span> {meaning}
                        </p>
                        <p className="phrase-example">
                            <span style={{fontWeight: '600', color: '#2d3436'}}>Example:</span> "{example}"
                        </p>
                    </div>

                    <div 
                        className={`phrase-image ${isHovered ? 'hovered' : ''} ${canShowImage ? 'has-image' : 'no-image'}`}
                        onMouseEnter={this.handleMouseEnter}
                        onMouseLeave={this.handleMouseLeave}
                    >
                        {/* Скрытая загрузка изображения для кэширования */}
                        {src && !imageLoaded && !imageError && (
                            <img 
                                style={{display: 'none'}}
                                src={src} 
                                alt={alt}
                                onLoad={this.handleImageLoad}
                                onError={this.handleImageError}
                            />
                        )}

                        <div className="image-container">
                            {showImage ? (
                                <img 
                                    src={src} 
                                    alt={alt}
                                    className="phrase-img"
                                />
                            ) : (
                                <div className="emoji-display">
                                    {emojis}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default WeeklyPhrase;
