import React, { Component } from "react";

class WeeklyPhrase extends Component {
    render() {
        const { name, level } = this.props

        return (
            <div className="lesson-container">
                <h2 className="lesson-title">Phrase Of The Week</h2>
                <div className="lesson-content">
                    <div className="lesson-text">
                        <p className="lesson-phrase">"When pigs fly"</p>
                        <p className="lesson-meaning">Meaning: Never.</p>
                        <p className="lesson-example">Example: "He'll clean his room when pigs fly."</p>
                    </div>
                    <div className="lesson-image">
                        <img src="/images/flying-pig.png" alt="Flying Pig" /> {/* Предполагается, что изображение находится в папке public/images */}
                    </div>
                </div>
                <button className="lesson-button">View more</button>
            </div>
        )
    }
}

export default WeeklyPhrase
