import React, { useState, useEffect } from "react";
import './StylesH.css';
import { MainButton } from "./MainButton";
import { TopActiveButton } from "./TopActiveButton";
import { TopPassButton } from "./TopPassButton";
import { SignInForm } from "./SignInForm";
import wit_img from './img/wit-Photoroom.png';
import Modal from "./Modal";

export const Header = (props) => {
    const [modal, setModal] = useState(false);

    const toggleModal = () => {
        setModal(!modal)
    }

    useEffect(() => {
        const handleEscape = (event) => {
            if (event.key === 'Escape') {
                setModal(false);
            }
        };

        if (modal) {
            document.addEventListener('keydown', handleEscape);
        }

        return () => {
            document.removeEventListener('keydown', handleEscape);
        };
    }, [modal]);


    if(modal) {
        document.body.classList.add('active-modal')
    }
    else {
        document.body.classList.remove('active-modal')
    }

    return (
        <header className="header-glass">
            <div className="glass-effect">
                <div className="header-content">
                    {/* Логотип */}
                    <div className="header-logo-section">
                        <img src={wit_img} alt="wit" className="header-logo"/>
                    </div>

                    {/* Навигация */}
                    <nav className="header-nav">
                        <TopActiveButton text='Lessons'/>
                        <TopPassButton text='Vocabulary'/>
                        <TopPassButton text='Practice'/>
                        <TopPassButton text='Progress'/>
                    </nav>

                    {/* Правая часть */}
                    <div className="header-user-section">
                        <span className="user-name">User Name</span>
                        <div className="sign-in-btn" onClick={toggleModal}>
                            Sign in
                        </div>
                    </div>
                </div>
            </div>

            {/* Модальное окно */}
            {modal && (
                <div className="modal">
                    <div onClick={toggleModal} className="overlay"></div>
                    <div className="modal-content">
                        <SignInForm/>
                    </div>
                </div>
            )}
        </header>
    )

    // return (
