import React, { Component } from "react";
import './LessonDetail.css'

class LessonDetail extends Component {
    state = {
        activeTab: 'fill-blanks',
        userAnswers: {},
        currentTask: 0
    }

    tasks = [
        {
            type: 'fill-blanks',
            title: 'Complete the sentences',
            content: 'Fill in the blanks with the correct words from the lesson.',
            data: {
                sentence: "The cat ___ on the ___ and ___ peacefully.",
                blanks: [
                    { id: 1, correct: 'sleeps' },
                    { id: 2, correct: 'mat' },
                    { id: 3, correct: 'dreams' }
                ]
            }
        },
        {
            type: 'multiple-choice',
            title: 'Choose the correct answer',
            content: 'Select the right option based on the grammar rules we learned.',
            data: {
                question: "Which sentence is in Present Simple tense?",
                options: [
                    { id: 1, text: "I am going to school", correct: false },
                    { id: 2, text: "She goes to school every day", correct: true },
                    { id: 3, text: "They went to school yesterday", correct: false },
                    { id: 4, text: "We will go to school tomorrow", correct: false }
                ]
            }
        },
        {
            type: 'reading',
            title: 'Reading Comprehension',
            content: 'Read the text and answer the questions below.',
            data: {
                text: "The quick brown fox jumps over the lazy dog. This sentence contains every letter in the English alphabet. It's often used to test typewriters and keyboards.",
                questions: [
                    { id: 1, question: "What does the fox do?", correct: "jumps over the lazy dog" },
                    { id: 2, question: "Why is this sentence special?", correct: "contains every letter" }
                ]
            }
        }
    ]

    handleTabChange = (tab) => {
        this.setState({ activeTab: tab });
    }

    handleAnswer = (questionId, answer) => {
        this.setState(prevState => ({
            userAnswers: {
                ...prevState.userAnswers,
                [questionId]: answer
            }
        }));
    }

    nextTask = () => {
        this.setState(prevState => ({
            currentTask: Math.min(prevState.currentTask + 1, this.tasks.length - 1)
        }));
    }

    prevTask = () => {
        this.setState(prevState => ({
            currentTask: Math.max(prevState.currentTask - 1, 0)
        }));
    }

    renderTheory() {
        return (
            <div className="theory-content">
                <div className="theory-header">
                    <h2>Present Simple Tense</h2>
                    <div className="difficulty-badge intermediate">Intermediate</div>
                </div>

                <div className="theory-section">
                    <h3>üìö Definition</h3>
                    <p>Present Simple is used for habits, general truths, repeated actions, and fixed arrangements.</p>
                </div>

                <div className="theory-section">
                    <h3>üéØ Usage</h3>
                    <ul>
                        <li>Habits: <em>"I drink coffee every morning."</em></li>
                        <li>General truths: <em>"The sun rises in the east."</em></li>
                        <li>Fixed arrangements: <em>"The train leaves at 8 PM."</em></li>
                    </ul>
                </div>

                <div className="theory-section">
                    <h3>‚úçÔ∏è Structure</h3>
                    <div className="structure-example">
                        <div className="positive">
                            <strong>Positive:</strong> Subject + V1 (s/es for he/she/it)
                        </div>
                        <div className="negative">
                            <strong>Negative:</strong> Subject + do/does + not + V1
                        </div>
                        <div className="question">
                            <strong>Question:</strong> Do/Does + subject + V1?
                        </div>
                    </div>
                </div>

                <div className="theory-section">
                    <h3>üåü Examples</h3>
                    <div className="examples-grid">
                        <div className="example-card">
                            <div className="example-text">"She works in an office."</div>
                            <div className="example-note">Habitual action</div>
                        </div>
                        <div className="example-card">
                            <div className="example-text">"Water boils at 100¬∞C."</div>
                            <div className="example-note">General truth</div>
                        </div>
                        <div className="example-card">
                            <div className="example-text">"Do you like pizza?"</div>
                            <div className="example-note">Question form</div>
                        </div>
                    </div>
                </div>

                <div className="theory-tips">
                    <h3>üí° Tips</h3>
                    <p>Remember to add -s or -es for he/she/it in positive sentences!</p>
                </div>
            </div>
        );
    }

    renderPractice() {
        const { activeTab, currentTask, userAnswers } = this.state;
        const currentTaskData = this.tasks[currentTask];

        const renderTaskContent = () => {
            switch (currentTaskData.type) {
                case 'fill-blanks':
                    return (
                        <div className="task-fill-blanks">
                            <h3>{currentTaskData.title}</h3>
                            <p>{currentTaskData.content}</p>
                            <div className="sentence-with-blanks">
                                {currentTaskData.data.sentence.split('___').map((part, index, array) => (
                                    <span key={index}>
                                        {part}
                                        {index < array.length - 1 && (
                                            <input
                                                type="text"
                                                className="blank-input"
                                                placeholder="..."
                                                value={userAnswers[index] || ''}
                                                onChange={(e) => this.handleAnswer(index, e.target.value)}
                                            />
                                        )}
                                    </span>
                                ))}
                            </div>
                            <button className="check-answers-btn">Check Answers</button>
                        </div>
                    );

                case 'multiple-choice':
                    return (
                        <div className="task-multiple-choice">
                            <h3>{currentTaskData.title}</h3>
                            <p>{currentTaskData.content}</p>
                            <div className="question-text">{currentTaskData.data.question}</div>
                            <div className="options-grid">
                                {currentTaskData.data.options.map(option => (
                                    <label key={option.id} className="option-label">
                                        <input
                                            type="radio"
                                            name="mc-question"
                                            value={option.id}
                                            onChange={() => this.handleAnswer('mc', option.id)}
                                        />
                                        <span className="option-text">{option.text}</span>
                                    </label>
                                ))}
                            </div>
                            <button className="check-answers-btn">Check Answer</button>
                        </div>
                    );

                case 'reading':
                    return (
                        <div className="task-reading">
                            <h3>{currentTaskData.title}</h3>
                            <p>{currentTaskData.content}</p>
                            <div className="reading-text">
                                {currentTaskData.data.text}
                            </div>
                            <div className="questions-list">
                                {currentTaskData.data.questions.map(q => (
                                    <div key={q.id} className="question-item">
                                        <div className="question">{q.question}</div>
                                        <input
                                            type="text"
                                            className="answer-input"
                                            placeholder="Your answer..."
                                            value={userAnswers[q.id] || ''}
                                            onChange={(e) => this.handleAnswer(q.id, e.target.value)}
                                        />
                                    </div>
                                ))}
                            </div>
                            <button className="check-answers-btn">Check Answers</button>
                        </div>
                    );

                default:
                    return <div>Unknown task type</div>;
            }
        };

        return (
            <div className="practice-content">
                <div className="practice-header">
                    <h2>Practice Exercises</h2>
                    <div className="progress-indicator">
                        Task {currentTask + 1} of {this.tasks.length}
                    </div>
                </div>

                <div className="tabs-container">
                    {this.tasks.map((task, index) => (
                        <button
                            key={task.type}
                            className={`tab-button ${currentTask === index ? 'active' : ''}`}
                            onClick={() => this.setState({ currentTask: index })}
                        >
                            {task.title}
                        </button>
                    ))}
                </div>

                <div className="task-content">
                    {renderTaskContent()}
                </div>

                <div className="navigation-buttons">
                    <button 
                        className="nav-btn prev-btn"
                        onClick={this.prevTask}
                        disabled={currentTask === 0}
                    >
                        ‚Üê Previous
                    </button>
                    <button 
                        className="nav-btn next-btn"
                        onClick={this.nextTask}
                        disabled={currentTask === this.tasks.length - 1}
                    >
                        Next ‚Üí
                    </button>
                </div>
            </div>
        );
    }

    render() {
        return (
            <div className='lesson-detail-page'>
                <div className='lesson-detail-left'>
                    {this.renderTheory()}
                </div>
                <div className='lesson-detail-right'>
                    {this.renderPractice()}
                </div>
            </div>
        )
    }
}

export default LessonDetail;
