import React, { useState } from 'react';
import { FaApple, FaGoogle, FaGithub } from 'react-icons/fa'; // Импорт иконок
import './SignInForm.css'; // Импорт стилей

const SignInForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Здесь можно добавить логику аутентификации
    console.log('Попытка входа:', { email, password });
    alert(`Вход с email: ${email}, паролем: ${password}`);
  };

  const handleSocialLogin = (provider) => {
    console.log(`Вход через ${provider}`);
    alert(`Вход через ${provider} пока не реализован.`);
    // Здесь можно добавить логику для входа через соцсети
  };

  const handleSignUp = () => {
    console.log('Переход на страницу регистрации');
    alert('Переход на страницу регистрации пока не реализован.');
    // Здесь можно добавить логику для перехода на страницу регистрации
  };

  return (
    <div className="auth-page-container">
      <div className="auth-header">Auth</div> {/* Текст "Auth" в левом верхнем углу */}

      <div className="sign-in-form-wrapper">
        {/* Декоративные линии по углам */}
        <div className="decorative-border top-left"></div>
        <div className="decorative-border top-right"></div>
        <div className="decorative-border bottom-left"></div>
        <div className="decorative-border bottom-right"></div>

        <form onSubmit={handleSubmit} className="sign-in-form">
          <h2>Sign in</h2>

          <div className="input-group">
            <label htmlFor="email">login/email</label>
            <input
              type="text"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <div className="form-actions">
            <button type="submit" className="sign-in-button">
              SIGN IN
              <span className="button-outline"></span> {/* Для черной обводки */}
            </button>
            <a href="#" className="forgot-password">forgot password?</a>
          </div>

          <div className="separator">or continue with:</div>

          <div className="social-login-icons">
            <FaApple className="social-icon" onClick={() => handleSocialLogin('Apple')} />
            <FaGoogle className="social-icon" onClick={() => handleSocialLogin('Google')} />
            <FaGithub className="social-icon" onClick={() => handleSocialLogin('GitHub')} />
          </div>

          <div className="no-account-prompt">
            haven't got an account?
          </div>
          <button type="button" className="sign-up-button" onClick={handleSignUp}>
            SIGN UP
            <span className="button-outline"></span> {/* Для черной обводки */}
          </button>
        </form>
      </div>
    </div>
  );
};

export default SignInForm;
