import React, { useState, useEffect } from 'react';
import './LessonsList.css';
import Lesson from './Lesson';

const LessonsList = () => {
  const [lessons, setLessons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLessons = async () => {
      try {
        console.log('üîÑ –ó–∞–≥—Ä—É–∂–∞—é —É—Ä–æ–∫–∏...');

        const response = await fetch('http://localhost:8080/api/lessons');

        if (!response.ok) {
          throw new Error(`HTTP –æ—à–∏–±–∫–∞! –°—Ç–∞—Ç—É—Å: ${response.status}`);
        }

        const data = await response.json();
        console.log('‚úÖ –ü–æ–ª—É—á–µ–Ω—ã —É—Ä–æ–∫–∏:', data);

        // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Å—Ç—Ä—É–∫—Ç—É—Ä—É –¥–∞–Ω–Ω—ã—Ö
        data.forEach(lesson => {
          console.log(`–£—Ä–æ–∫ ${lesson.title}:`, {
            id: lesson.id,
            skillsCount: lesson.skills?.length || 0,
            skills: lesson.skills?.map(s => s.name) || []
          });
        });

        setLessons(data);

      } catch (err) {
        console.error('‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchLessons();
  }, []);

  if (loading) return (
    <div className="loading-state">
      <div className="spinner"></div>
      <p>–ó–∞–≥—Ä—É–∂–∞–µ–º —É—Ä–æ–∫–∏...</p>
    </div>
  );

  if (error) return (
    <div className="error-state">
      <p>–û—à–∏–±–∫–∞: {error}</p>
      <button onClick={() => window.location.reload()}>–û–±–Ω–æ–≤–∏—Ç—å</button>
    </div>
  );

  if (lessons.length === 0) return (
    <div className="empty-state">
      <p>–£—Ä–æ–∫–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</p>
    </div>
  );

  return (
    <div className="lessons-container">
      <div className="lessons-header">
        <h2>–î–æ—Å—Ç—É–ø–Ω—ã–µ —É—Ä–æ–∫–∏</h2>
        <span className="lessons-count">{lessons.length} —É—Ä–æ–∫–æ–≤</span>
      </div>

      <div className="lessons-grid">
        {lessons.map(lesson => {
          // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –Ω–∞–≤—ã–∫–∏ –∏–∑ –æ–±—ä–µ–∫—Ç–æ–≤ –≤ –º–∞—Å—Å–∏–≤ —Å—Ç—Ä–æ–∫
          const skillNames = lesson.skills 
            ? lesson.skills.map(skill => skill.name)
            : [];

          return (
            <Lesson 
              key={lesson.id}
              lessonNumber={lesson.number}
              label='Freshman'  // –ü–æ–∫–∞ —Å—Ç–∞—Ç–∏—á–µ—Å–∫–æ–µ, –ø–æ—Ç–æ–º –º–æ–∂–Ω–æ –∏–∑–º–µ–Ω–∏—Ç—å
              title={lesson.title}
              description={lesson.description}
              skills={skillNames}  // –¢–µ–ø–µ—Ä—å —Ä–µ–∞–ª—å–Ω—ã–µ –Ω–∞–≤—ã–∫–∏ –∏–∑ –ë–î!
              imageSrc={lesson.imgSrc}
              score='2'
              level='7'
              state='3'
            />
          );
        })}
      </div>
    </div>
  );
};

export default LessonsList;
