import React, { Component } from "react";
import { MainButton } from "./MainButton";
import './StylesWP.css'

class WeeklyPhrase extends Component {
    handleImageError = (e) => {
        // –ë–µ–∑–æ–ø–∞—Å–Ω–∞—è –æ–±—Ä–∞–±–æ—Ç–∫–∞ –æ—à–∏–±–∫–∏
        const parent = e.target.parentNode;
        if (parent) {
            e.target.style.display = 'none';
            parent.innerHTML = 'üê∑‚ú®';
            parent.style.fontSize = '3rem';
            parent.style.display = 'flex';
            parent.style.alignItems = 'center';
            parent.style.justifyContent = 'center';
        } else {
            // –ï—Å–ª–∏ parentNode –Ω–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç, –ø—Ä–æ—Å—Ç–æ —Å–∫—Ä—ã–≤–∞–µ–º –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
            e.target.style.display = 'none';
        }
    }

    render() {
        const { name, level } = this.props

        return (
            <div className="phrase-container">
                <div className="phrase-title">Phrase Of The Week</div>

                <div className="phrase-content">
                    <div className="phrase-text">
                        <p className="phrase-phrase">"When pigs fly"</p>
                        <p className="phrase-meaning">
                            <span style={{fontWeight: '600', color: '#2d3436'}}>Meaning:</span> Never.
                        </p>
                        <p className="phrase-example">
                            <span style={{fontWeight: '600', color: '#2d3436'}}>Example:</span> "He'll clean his room when pigs fly."
                        </p>
                    </div>

                    <div className="phrase-image">
                        <img 
                            src="/images/flying-pig.png" 
                            alt="Flying Pig Illustration" 
                            onError={(e) => {
                                e.target.style.display = 'none';
                                e.target.parentNode.innerHTML = 'üê∑‚ú®';
                                e.target.parentNode.style.fontSize = '3rem';
                            }}
                        />
                    </div>
                </div>

                {/* <div style={{textAlign: 'center'}}>
                    <MainButton text="View More" />
                </div> */}
            </div>

            // <div className="phrase-container">
            //     <div className="phrase-title">Phrase Of The Week</div>
            //     <div className="phrase-content">
            //         <div className="phrase-text">
            //             <p className="phrase-phrase">"When pigs fly"</p>
            //             <p className="phrase-meaning">Meaning: Never.</p>
            //             <p className="phrase-example">Example: "He'll clean his room when pigs fly."</p>
            //         </div>
            //         <div className="phrase-image">
            //             <img src="/images/flying-pig.png" alt="Flying Pig" /> {/* –ü—Ä–µ–¥–ø–æ–ª–∞–≥–∞–µ—Ç—Å—è, —á—Ç–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –≤ –ø–∞–ø–∫–µ public/images */}
            //         </div>
            //     </div>
            //     <MainButton text="View More"/>
            // </div>
        )
    }
}

export default WeeklyPhrase
