import React, { Component } from "react";
import './LessonDetail.css'

class LessonDetail extends Component {
    state = {
        currentTask: 0
    }

    // –î–∞–Ω–Ω—ã–µ –¥–ª—è —Ç–µ–æ—Ä–∏–∏ (–º–æ–∂–Ω–æ –ø–µ—Ä–µ–¥–∞–≤–∞—Ç—å —á–µ—Ä–µ–∑ props)
    theoryData = {
        title: "Present Simple Tense",
        difficulty: "intermediate",
        sections: [
            {
                icon: "üìö",
                title: "Definition",
                content: "Present Simple is used for habits, general truths, repeated actions, and fixed arrangements."
            },
            {
                icon: "üéØ",
                title: "Usage",
                type: "list",
                content: [
                    "Habits: \"I drink coffee every morning.\"",
                    "General truths: \"The sun rises in the east.\"",
                    "Fixed arrangements: \"The train leaves at 8 PM.\""
                ]
            },
            {
                icon: "‚úçÔ∏è",
                title: "Structure",
                type: "structure",
                content: {
                    positive: "Subject + V1 (s/es for he/she/it)",
                    negative: "Subject + do/does + not + V1",
                    question: "Do/Does + subject + V1?"
                }
            },
            {
                icon: "üåü",
                title: "Examples",
                type: "examples",
                content: [
                    { text: "\"She works in an office.\"", note: "Habitual action" },
                    { text: "\"Water boils at 100¬∞C.\"", note: "General truth" },
                    { text: "\"Do you like pizza?\"", note: "Question form" }
                ]
            },
            {
                icon: "üí°",
                title: "Tips",
                content: "Remember to add -s or -es for he/she/it in positive sentences!",
                isTip: true
            }
        ]
    }

    // –î–∞–Ω–Ω—ã–µ –¥–ª—è –ø—Ä–∞–∫—Ç–∏–∫–∏ (–º–æ–∂–Ω–æ –ø–µ—Ä–µ–¥–∞–≤–∞—Ç—å —á–µ—Ä–µ–∑ props)
    practiceData = [
        {
            id: 1,
            title: "Fill in the Blanks",
            type: "fill-blanks",
            content: {
                instruction: "Complete the sentences with the correct words from the lesson.",
                sentence: "The cat ___ on the ___ and ___ peacefully.",
                blanks: [
                    { id: 1, correct: 'sleeps' },
                    { id: 2, correct: 'mat' },
                    { id: 3, correct: 'dreams' }
                ]
            }
        },
        {
            id: 2,
            title: "Multiple Choice",
            type: "multiple-choice",
            content: {
                instruction: "Select the right option based on the grammar rules we learned.",
                question: "Which sentence is in Present Simple tense?",
                options: [
                    { id: 1, text: "I am going to school", correct: false },
                    { id: 2, text: "She goes to school every day", correct: true },
                    { id: 3, text: "They went to school yesterday", correct: false },
                    { id: 4, text: "We will go to school tomorrow", correct: false }
                ]
            }
        },
        {
            id: 3,
            title: "Reading Comprehension",
            type: "reading",
            content: {
                instruction: "Read the text and answer the questions below.",
                text: "The quick brown fox jumps over the lazy dog. This sentence contains every letter in the English alphabet. It's often used to test typewriters and keyboards.",
                questions: [
                    { id: 1, question: "What does the fox do?", correct: "jumps over the lazy dog" },
                    { id: 2, question: "Why is this sentence special?", correct: "contains every letter" }
                ]
            }
        }
    ]

    handleTaskChange = (taskIndex) => {
        this.setState({ currentTask: taskIndex });
    }

    nextTask = () => {
        this.setState(prevState => ({
            currentTask: Math.min(prevState.currentTask + 1, this.practiceData.length - 1)
        }));
    }

    prevTask = () => {
        this.setState(prevState => ({
            currentTask: Math.max(prevState.currentTask - 1, 0)
        }));
    }

    // –£–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω—ã–π —Ä–µ–Ω–¥–µ—Ä –¥–ª—è —Ç–µ–æ—Ä–∏–∏
    renderTheory = (theoryData) => {
        const { title, difficulty, sections } = theoryData;

        return (
            <div className="theory-content">
                <div className="theory-header">
                    <h2>{title}</h2>
                    <div className={`difficulty-badge ${difficulty}`}>
                        {difficulty.charAt(0).toUpperCase() + difficulty.slice(1)}
                    </div>
                </div>

                {sections.map((section, index) => (
                    <div key={index} className={`theory-section ${section.isTip ? 'theory-tips' : ''}`}>
                        <h3>{section.icon} {section.title}</h3>

                        {section.type === 'list' ? (
                            <ul>
                                {section.content.map((item, i) => (
                                    <li key={i}>{item}</li>
                                ))}
                            </ul>
                        ) : section.type === 'structure' ? (
                            <div className="structure-example">
                                <div className="positive">
                                    <strong>Positive:</strong> {section.content.positive}
                                </div>
                                <div className="negative">
                                    <strong>Negative:</strong> {section.content.negative}
                                </div>
                                <div className="question">
                                    <strong>Question:</strong> {section.content.question}
                                </div>
                            </div>
                        ) : section.type === 'examples' ? (
                            <div className="examples-grid">
                                {section.content.map((example, i) => (
                                    <div key={i} className="example-card">
                                        <div className="example-text">{example.text}</div>
                                        <div className="example-note">{example.note}</div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <p>{section.content}</p>
                        )}
                    </div>
                ))}
            </div>
        );
    }

    // –£–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω—ã–π —Ä–µ–Ω–¥–µ—Ä –¥–ª—è –ø—Ä–∞–∫—Ç–∏–∫–∏
    renderPractice = (practiceData, currentTask, userAnswers = {}) => {
        const currentTaskData = practiceData[currentTask];

        const renderTaskContent = () => {
            switch (currentTaskData.type) {
                case 'fill-blanks':
                    return (
                        <div className="task-fill-blanks">
                            <p className="instruction">{currentTaskData.content.instruction}</p>
                            <div className="sentence-with-blanks">
                                {currentTaskData.content.sentence.split('__+_').map((part, index, array) => (
                                    <span key={index}>
                                        {part}
                                        {index < array.length - 1 && (
                                            <input
                                                type="text"
                                                className="blank-input"
                                                placeholder="..."
                                                value={userAnswers[index] || ''}
                                                onChange={(e) => this.handleAnswer(index, e.target.value)}
                                            />
                                        )}
                                    </span>
                                ))}
                            </div>
                            <button className="check-answers-btn">Check Answers</button>
                        </div>
                    );

                case 'multiple-choice':
                    return (
                        <div className="task-multiple-choice">
                            <p className="instruction">{currentTaskData.content.instruction}</p>
                            <div className="question-text">{currentTaskData.content.question}</div>
                            <div className="options-grid">
                                {currentTaskData.content.options.map(option => (
                                    <label key={option.id} className="option-label">
                                        <input
                                            type="radio"
                                            name="mc-question"
                                            value={option.id}
                                            onChange={() => this.handleAnswer('mc', option.id)}
                                        />
                                        <span className="option-text">{option.text}</span>
                                    </label>
                                ))}
                            </div>
                            <button className="check-answers-btn">Check Answer</button>
                        </div>
                    );

                case 'reading':
                    return (
                        <div className="task-reading">
                            <p className="instruction">{currentTaskData.content.instruction}</p>
                            <div className="reading-text">
                                {currentTaskData.content.text}
                            </div>
                            <div className="questions-list">
                                {currentTaskData.content.questions.map(q => (
                                    <div key={q.id} className="question-item">
                                        <div className="question">{q.question}</div>
                                        <input
                                            type="text"
                                            className="answer-input"
                                            placeholder="Your answer..."
                                            value={userAnswers[q.id] || ''}
                                            onChange={(e) => this.handleAnswer(q.id, e.target.value)}
                                        />
                                    </div>
                                ))}
                            </div>
                            <button className="check-answers-btn">Check Answers</button>
                        </div>
                    );

                default:
                    return <div>Unknown task type</div>;
            }
        };

        return (
            <div className="practice-content">
                <div className="practice-header">
                    <h2>Practice Exercises</h2>
                    <div className="progress-indicator">
                        Task {currentTask + 1} of {practiceData.length}
                    </div>
                </div>

                {/* –í–∫–ª–∞–¥–∫–∏-–∑–∞–∫–ª–∞–¥–∫–∏ */}
                <div className="bookmark-tabs">
                    {practiceData.map((task, index) => (
                        <div
                            key={task.id}
                            className={`bookmark-tab ${currentTask === index ? 'active' : ''}`}
                            onClick={() => this.handleTaskChange(index)}
                        >
                            <div className="bookmark-tab-content">
                                <span className="tab-number">{index + 1}</span>
                                <span className="tab-title">{task.title}</span>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="task-content">
                    {renderTaskContent()}
                </div>

                <div className="navigation-buttons">
                    <button 
                        className="nav-btn prev-btn"
                        onClick={this.prevTask}
                        disabled={currentTask === 0}
                    >
                        ‚Üê Previous
                    </button>
                    <button 
                        className="nav-btn next-btn"
                        onClick={this.nextTask}
                        disabled={currentTask === practiceData.length - 1}
                    >
                        Next ‚Üí
                    </button>
                </div>
            </div>
        );
    }

    handleAnswer = (questionId, answer) => {
        // –õ–æ–≥–∏–∫–∞ –æ–±—Ä–∞–±–æ—Ç–∫–∏ –æ—Ç–≤–µ—Ç–æ–≤
        console.log(`Answer for ${questionId}: ${answer}`);
    }

    render() {
        const { currentTask } = this.state;

        return (
            <div className='lesson-detail-page'>
                <div className='lesson-detail-left'>
                    {this.renderTheory(this.theoryData)}
                </div>
                <div className='lesson-detail-right'>
                    {this.renderPractice(this.practiceData, currentTask)}
                </div>
            </div>
        )
    }
}

export default LessonDetail;
