import React, { useState, useEffect } from 'react';
import './LessonsList.css';
import Lesson from './Lesson';

const LessonsList = () => {
  const [lessons, setLessons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLessons = async () => {
      try {
        console.log('üîÑ –ó–∞–≥—Ä—É–∂–∞—é —É—Ä–æ–∫–∏ —Å –±—ç–∫–µ–Ω–¥–∞...');

        const response = await fetch('http://localhost:8080/api/lessons');

        if (!response.ok) {
          throw new Error(`HTTP –æ—à–∏–±–∫–∞! –°—Ç–∞—Ç—É—Å: ${response.status}`);
        }

        const data = await response.json();
        console.log('‚úÖ –ü–æ–ª—É—á–µ–Ω—ã —É—Ä–æ–∫–∏:', data);
        setLessons(data);

      } catch (err) {
        console.error('‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchLessons();
  }, []);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>–ó–∞–≥—Ä—É–∂–∞–µ–º —É—Ä–æ–∫–∏ —Å —Å–µ—Ä–≤–µ—Ä–∞...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h3>–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏</h3>
        <p>{error}</p>
        <button onClick={() => window.location.reload()}>
          –ü–æ–ø—Ä–æ–±–æ–≤–∞—Ç—å —Å–Ω–æ–≤–∞
        </button>
      </div>
    );
  }

  return (
    <div className="lessons-container">
      {/* <div className="lessons-title">{lessons.length} lessons available</div> */}

      {lessons.map(lesson => (
        <Lesson lessonNumber={lesson.number}
          label='Freshman'
          title={lesson.title}
          description={lesson.description}
          skills={['vocabulary', 'reading', 'listening']}
          imageSrc={lesson.imgSrc}
          score='2'
          level='7'
          state='3' />
      ))}

      {lessons.map(lesson => (
        <div key={lesson.id} className="lesson-card">
          {/* ... –æ—Å—Ç–∞–ª—å–Ω—ã–µ –ø–æ–ª—è ... */}

          {/* –ù–û–í–û–ï: –û—Ç–æ–±—Ä–∞–∂–∞–µ–º –Ω–∞–≤—ã–∫–∏ */}
          {lesson.skills && lesson.skills.length > 0 && (
            <div className="lesson-skills">
              <p className="skills-label">–ù–∞–≤—ã–∫–∏ –≤ —ç—Ç–æ–º —É—Ä–æ–∫–µ:</p>
              <div className="skills-list">
                {lesson.skills.map(skill => (
                  <span key={skill.id} className="skill-tag">
                    {skill.name}
                  </span>
                ))}
              </div>
            </div>
          )}

          {/* ... –æ—Å—Ç–∞–ª—å–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã ... */}
        </div>
      ))}


      {/* <div className="lessons-grid">
        {lessons.map(lesson => (
          <div key={lesson.id} className="lesson-card">
            <div className="lesson-header">
              <span className="lesson-number">–£—Ä–æ–∫ #{lesson.number}</span>
              <span className="lesson-level">{lesson.level}</span>
            </div>

            <h3 className="lesson-title">{lesson.title}</h3>

            {lesson.description && (
              <p className="lesson-description">{lesson.description}</p>
            )}

            {lesson.imgSrc && (
              <div className="lesson-image">
                <img
                  src={lesson.imgSrc}
                  alt={lesson.title}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = '/default-lesson.jpg';
                  }}
                />
              </div>
            )}

            <div className="lesson-footer">
              <button className="start-button">
                –ù–∞—á–∞—Ç—å —É—Ä–æ–∫
              </button>
            </div>
          </div>
        ))}
      </div> */}
    </div>
  );
};

export default LessonsList;
