import React, { useState } from "react";
import './Styles.css';
import { MainButton } from "./MainButton";
import { TopActiveButton } from "./TopActiveButton";
import { TopPassButton } from "./TopPassButton";
import { SignInForm } from "./SignInForm";
import wit_img from './img/wit-Photoroom.png';
import Modal from "./Modal";

export const Header = (props) => {
    const [modal, setModal] = useState(false);

    const toggleModal = () => {
        setModal(!modal)
    }

        useEffect(() => {
        const handleEscape = (event) => {
            // Проверяем, если нажата клавиша Escape
            if (event.key === 'Escape') {
                // Закрываем модальное окно
                setModal(false);
            }
        };

        // Добавляем слушатель событий только если модальное окно открыто
        if (modal) {
            document.addEventListener('keydown', handleEscape);
        }

        // Функция очистки: удаляем слушатель событий, когда компонент размонтируется
        // или когда `modal` меняется (и становится false)
        return () => {
            document.removeEventListener('keydown', handleEscape);
        };
    }, [modal]);

    return (
        <div className='origin'>
            <ul>
                <li><img src={wit_img} alt="wit" className="header-logo"/></li>
                <li><TopActiveButton text='Lesson'/></li>
                <li><TopPassButton text='Lesson'/></li>
                <li><TopPassButton text='Lesson'/></li>
                <li>User Name</li>
                <li><MainButton text='Sign in' onClick={toggleModal}/></li>
                <button onClick={toggleModal}>Click</button>


      {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay"></div>
          <div className="modal-content">
            <h2>Hello Modal</h2>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Provident
              perferendis suscipit officia recusandae, eveniet quaerat assumenda
              id fugit, dignissimos maxime non natus placeat illo iusto!
              Sapiente dolorum id maiores dolores? Illum pariatur possimus
              quaerat ipsum quos molestiae rem aspernatur dicta tenetur. Sunt
              placeat tempora vitae enim incidunt porro fuga ea.
            </p>
            <button className="close-modal" onClick={toggleModal}>
              CLOSE
            </button>
          </div>
        </div>
      )}
                {/* <li><MainButton text='eng'/></li> */}
            </ul>
        </div>
    )
}
