import React, { useState, useEffect } from "react";
import './StylesH.css';
import { MainButton } from "./MainButton";
import { TopActiveButton } from "./TopActiveButton";
import { TopPassButton } from "./TopPassButton";
import { SignInForm } from "./SignInForm";
import wit_img from './img/wit-Photoroom.png';
import Modal from "./Modal";

export const Header = (props) => {
    const [modal, setModal] = useState(false);
    const [isScrolled, setIsScrolled] = useState(false);

    const toggleModal = () => {
        setModal(!modal)
    }

    // Эффект для изменения хедера при скролле
    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    useEffect(() => {
        const handleEscape = (event) => {
            if (event.key === 'Escape') {
                setModal(false);
            }
        };

        if (modal) {
            document.addEventListener('keydown', handleEscape);
        }

        return () => {
            document.removeEventListener('keydown', handleEscape);
        };
    }, [modal]);

    if(modal) {
        document.body.classList.add('active-modal')
    }
    else {
        document.body.classList.remove('active-modal')
    }

// Альтернативная версия хедера в том же файле
return (
    <header className="header-minimal">
        <div className="header-container">
            <div className="header-brand">
                <img src={wit_img} alt="wit" className="header-logo"/>
                <span className="brand-name">EnglishWit</span>
            </div>

            <nav className="header-nav-minimal">
                <TopActiveButton text='Learn'/>
                <TopPassButton text='Practice'/>
                <TopPassButton text='Progress'/>
            </nav>

            <div className="header-user-minimal">
                <div className="user-avatar-minimal">U</div>
                <div className="sign-in-minimal" onClick={toggleModal}>
                    Sign In
                </div>
            </div>
        </div>
    </header>
)

}

