import './App.css';
import { Component } from 'react';
import { Header } from './components/Header'
import Lesson from './components/LessonBanner'
import Banner from './components/Banner';
import { SignInForm } from './components/SignInForm';
import WeeklyPhrase from './components/WeeklyPhrase';
import img9 from './components/img/image9.jpg'

class App extends Component {
  render() {
    return (
      <div className="App">

      <div className="App">
        <header className="App-header">
          <div className="auth-text-on-page">Auth</div> {/* Текст "Auth" на главной странице */}
          <h1>Добро пожаловать!</h1>
          <p>Нажмите кнопку ниже, чтобы войти.</p>
          <button className="open-modal-button" onClick={() => setIsSignInModalOpen(true)}>
            Открыть форму входа
          </button>
        </header>

        {/* Модальное окно с формой входа */}
        <Modal isOpen={isSignInModalOpen} onClose={() => setIsSignInModalOpen(false)}>
          <SignInForm />
        </Modal>
      </div>

        <Header/>
        <SignInForm/>
        <Banner/>
        <WeeklyPhrase/>
        <Lesson lessonNumber='3' 
                label='Freshman' 
                title='Animals' 
                description='Learning all about animals: their names, habitats and other vocabulary (lalala)' 
                skills={['vocabulary', 'reading', 'writing skills', 'listening']} 
                imageSrc={img9} 
                score='2' 
                level='7'
                state='3'/>


      </div>
    );
  }
}

export default App;
